import { useState } from "react";
import "./App.css";
import twoC from "./img/2c.png";
import twoD from "./img/2d.png";
import twoH from "./img/2h.png";
import twoS from "./img/2s.png";

import threeC from "./img/3c.png";
import threeD from "./img/3d.png";
import threeH from "./img/3h.png";
import threeS from "./img/3s.png";

import fourC from "./img/4c.png";
import fourD from "./img/4d.png";
import fourH from "./img/4h.png";
import fourS from "./img/4s.png";

import fiveC from "./img/5c.png";
import fiveD from "./img/5d.png";
import fiveH from "./img/5h.png";
import fiveS from "./img/5s.png";

import sixC from "./img/6c.png";
import sixD from "./img/6d.png";
import sixH from "./img/6h.png";
import sixS from "./img/6s.png";

import sevenC from "./img/7c.png";
import sevenD from "./img/7d.png";
import sevenH from "./img/7h.png";
import sevenS from "./img/7s.png";

import eightC from "./img/8c.png";
import eightD from "./img/8d.png";
import eightH from "./img/8h.png";
import eightS from "./img/8s.png";

import nineC from "./img/9c.png";
import nineD from "./img/9d.png";
import nineH from "./img/9h.png";
import nineS from "./img/9s.png";

import tenC from "./img/10c.png";
import tenD from "./img/10d.png";
import tenH from "./img/10h.png";
import tenS from "./img/10s.png";

import jackC from "./img/jc.png";
import jackD from "./img/jd.png";
import jackH from "./img/jh.png";
import jackS from "./img/js.png";

import queenC from "./img/qc.png";
import queenD from "./img/qd.png";
import queenH from "./img/qh.png";
import queenS from "./img/qs.png";

import kingC from "./img/kc.png";
import kingD from "./img/kd.png";
import kingH from "./img/kh.png";
import kingS from "./img/ks.png";

import aceC from "./img/ac.png";
import aceD from "./img/ad.png";
import aceH from "./img/ah.png";
import aceS from "./img/as.png";

let cards = [
  threeS,
  threeC,
  threeD,
  threeH,

  fourS,
  fourC,
  fourD,
  fourH,

  fiveS,
  fiveC,
  fiveD,
  fiveH,

  sixS,
  sixC,
  sixD,
  sixH,

  sevenS,
  sevenC,
  sevenD,
  sevenH,

  eightS,
  eightC,
  eightD,
  eightH,

  nineS,
  nineC,
  nineD,
  nineH,

  tenS,
  tenC,
  tenD,
  tenH,

  jackS,
  jackC,
  jackD,
  jackH,

  queenS,
  queenC,
  queenD,
  queenH,

  kingS,
  kingC,
  kingD,
  kingH,

  aceS,
  aceC,
  aceD,
  aceH,

  twoS,
  twoC,
  twoD,
  twoH,
];

function App() {
  const [deck, setDeck] = useState(cards);

  const resetDeck = () => {
    setDeck(cards);
  };

  const filter = (index) => {
    setDeck((deck) => {
      return deck.filter((card, idx) => idx !== index);
    });
  };

  return (
    <div className="App">
      <div className="buttonControl">
        <button onClick={resetDeck}>Reset</button>
      </div>
      {deck.map((cardSrc, index) => (
        <img
          className="imageCard"
          src={cardSrc}
          alt="queen"
          width="150px"
          height="150px"
          onClick={() => filter(index)}
        />
      ))}
    </div>
  );
}

export default App;
